const initialItems = [
  { id: 1, description: "Academics", time: 12, done: false },
  { id: 2, description: "Exercise", time: 14, done: false },
];

export default App;
function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <Lists />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>‚åõTick-Tick‚åõ</h1>;
}
function Form() {
  return (
    <div className="add-form">
      <h3>
        <i>With discipline, you can achieve anything. </i>ü•∑
      </h3>
    </div>
  );
}

function Lists() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((task) => (
          <Task task={task} />
        ))}
      </ul>
    </div>
  );
}

function Task({ tasak }) {
  return <li>{task.description}</li>;
}

function Stats() {
  return (
    <footer className="stats">
      <em>You have completed X tasks</em>
    </footer>
  );
}
